import React, { Component } from "react";
import { Link } from "react-router-dom";
import { Col, Row, Container } from "../components/Grid";
import Jumbotron from "../components/Jumbotron";
import API from "../utils/API";
import Delete from "../components/Delete";
import { Input, TextArea, FormBtn } from "../components/Form";



class Detail extends Component {
  state = {
    post: {},
    title: "",
    body: "",
    date: ""
  };


  // When this component mounts, grab the book with the _id of this.props.match.params.id
  // e.g. localhost:3000/books/599dcb67f0f16317844583fc
  componentDidMount() {
    API.getPost(this.props.match.params.id)
      .then(res => this.setState({ post: res.data }))
      .catch(err => console.log(err));
  }

  deletePost = id => {
    API.deletePost(id)
      .then(res => this.loadPosts())
      .catch(err => console.log(err));
  };

  handleInputChange = event => {
    const { name, value } = event.target;
    this.setState({
      [name]: value
    });
  };

  handleFormSubmit = event => {
    event.preventDefault();
    if (this.state.title && this.state.body) {
      API.savePost({
        title: this.state.title,
        body: this.state.body,
        date: this.state.date
      })
        .then(res => this.loadPosts())
        .catch(err => console.log(err));
    }
  };

  render() {
    return (
      <Container fluid>
        <Row>
          <Col size="md-12">
            <Jumbotron>
              <h1>
                {this.state.post.title} by Chu
              </h1>
            </Jumbotron>
          </Col>
        </Row>
        <Row>
          <Col size="md-10 md-offset-1">
          <form>
              <Input
                value={this.state.title}
                onChange={this.handleInputChange}
                name="title"
                placeholder={this.state.post.title}
              />

              <TextArea
                value={this.state.body}
                onChange={this.handleInputChange}
                name="body"
                placeholder={this.state.post.body}
              />

            </form>
          </Col>
        </Row>
        <Row>
          <Col size="md-2">
            <FormBtn
                  disabled={!(this.state.body && this.state.title)}
                  onClick={this.handleFormSubmit}
                >
                  Save Post
                </FormBtn>
          </Col>
          <Col size="md-2">
            <Delete onClick={() => this.deletePost(this.props.match.params.id)} />
          </Col>
          <Col size="md-2">
            <Link to="/cms">← Back to Posts</Link>
          </Col>
        </Row>
      </Container>
    );
  }
}

export default Detail;
